	public void savePessoa(Pessoa pessoa) {
		Pessoa p = pessoaDAO.getPessoaByCode(SidConstants.PALAZZO_CODE);
		if(p == null){
			p = new Pessoa();
			p.setCode(SidConstants.PALAZZO_CODE);
			p.setName("Jose Palazzo Moreira de Oliveira");		
			pessoaDAO.savePessoa(p);
		}
	}
	
	public Message updatePessoaWithAreas(String pessoaCode, Integer[] areaIds){
		Message msg = new Message();
		try{
			Pessoa p = pessoaDAO.getPessoaByCode(pessoaCode);
			List<AreaPesquisa> areas = new ArrayList<AreaPesquisa>();
				AreaPesquisa ap = null;
				for(Integer i : areaIds){
					ap = areaPesquisaDAO.getAreaPesquisaById(i);
					areas.add(ap);
				}
			p.setAreas(new HashSet<AreaPesquisa>(areas));
			pessoaDAO.updatePessoa(p);
			msg.setType(MessageType.SUCCESS);
			msg.setMessage("Updated");
		} catch(Exception e){
			msg.setType(MessageType.ERROR);
			msg.setMessage("Error saving Areas");
		}
		return msg;
	}
	
